INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (13, 'O que é a teoria da gravidade?', 'A resposta correta é a teoria proposta por Isaac Newton para explicar a atração entre corpos massivos.', 3, '2023-11-19', 0);
INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (14, 'Quem foi Leonardo da Vinci?', 'A resposta correta é um renascentista italiano conhecido por suas contribuições nas artes e ciências.', 4, '2023-11-19', 0);
