INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (18, 'Quem foi Ada Lovelace?', 'A resposta correta é considerada a primeira programadora de computadores.', 3, '2023-11-19', 0);
INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (19, 'O que é a Lei de Moore?', 'A resposta correta é a observação de que o número de transistores em um chip de computador dobra aproximadamente a cada dois anos.', 4, '2023-11-19', 0);
INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (20, 'Quem foi Martin Luther King Jr.?', 'A resposta correta é um líder dos direitos civis nos Estados Unidos.', 5, '2023-11-19', 0);
