INSERT INTO flashcards (id, front, back, id_matter, dateshow, daysshow) VALUES (23, 'Quem foi Alan Turing?', 'A resposta correta é um pioneiro da ciência da computação e um decodificador-chefe durante a Segunda Guerra Mundial.', 3, '2023-11-19', 0);
